import React, { useState,useEffect } from 'react'
import useAxios from '../utils/useAxios'
import SearchExecDisp from './SearchExecDisp'
const {REACT_APP_API_KEY} = process.env;


const Search = () => {
    const baseUrl = 'https://www.googleapis.com/books/v1/volumes'
    const [searchSubject, setSearchSubject] = useState(null);
    const [searchTitle, setSearchTitle] = useState(null);
    const [searchAuthor, setSearchAuthor] = useState(null);
    const [query, setQuery] = useState(null);    
    const [url, setUrl] = useState(null);


    const handleClick = (e) => {
        console.log(e)
        var s,a,t;
        if(searchSubject){
            s = searchSubject.replace(/\s/g, '+').toLowerCase();
        }
        if(searchAuthor){
            a = searchAuthor.replace(/\s/g, '+').toLowerCase();
        }
        if(searchTitle){
            t = searchTitle.replace(/\s/g, '+').toLowerCase();
        }
        if(s && t && a){
        setUrl(baseUrl + '?q=' + s + t + a + '&key=' + REACT_APP_API_KEY )
        alert(query);
        }
    }

    return (
        <div>
            <span>Search for books </span> 
            <form className='col s12'>
                <div className="row">   
                    <input  style={{margin: 10}} className="input-field col s2" type="text" placeholder=" Author" name="author" />
                    <input  style={{margin: 10}} className="input-field col s2" type="text" placeholder=" Title" name="title" />
                    <input  style={{margin: 10}} className="input-field col s2" type="text" placeholder=" Subject" name="subject" /> 
                </div>
                <button type="submit" onClick={(e) => handleClick(e)}>
                Start Search
            </button>
            </form>
            {url?<SearchExecDisp url={url} />:null}
        </div>
    )
}

export default Search
