import React, { useState,useEffect } from 'react'
import useAxios from '../utils/useAxios'
import SearchExecDisp from './SearchExecDisp'
const {REACT_APP_API_KEY} = process.env;


const Search = () => {
    const baseUrl = 'https://www.googleapis.com/books/v1/volumes'
    const [searchSubject, setSearchSubject] = useState(null);
    const [searchTitle, setSearchTitle] = useState(null);
    const [searchAuthor, setSearchAuthor] = useState(null);
    const [query, setQuery] = useState('?q=');    
    const [url, setUrl] = useState(null);


    const handleClick = () => {
        var a, t, s;
        if(searchSubject){
            s = searchSubject.replace(/\s/g, '+').toLowerCase();
            setQuery(query + s)
        }
        if(searchAuthor){
            a = searchAuthor.replace(/\s/g, '+').toLowerCase();
            setQuery(query + a)
        }
        if(searchTitle){
            t = searchTitle.replace(/\s/g, '+').toLowerCase();
            setQuery(query + t)
        }
        setUrl(baseUrl + query + '&key=' + REACT_APP_API_KEY )
    }

    return (
        <div>
            <span>Search for books </span> 
            <form className='col s12'>
                <div className="row">   
                    <input  style={{margin: 10}} className="input-field col s2" type="text" placeholder=" Author" onChange={ e => setSearchAuthor(e.target.value)} />
                    <input  style={{margin: 10}} className="input-field col s2" type="text" placeholder=" Title" onChange={ e => setSearchTitle(e.target.value)} />
                    <input  style={{margin: 10}} className="input-field col s2" type="text" placeholder=" Subject" onChange={ e => setSearchSubject(e.target.value)} /> 
                </div>
                <button type="button" onClick={() => handleClick()}>
                Start Search
            </button>
            </form>
            <SearchExecDisp url={url} />
        </div>
    )
}

export default Search
